@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700;800&family=Inter:wght@300;400;500;600&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom scrollbar for modal */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: rgba(255, 255, 255, 0.1);
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, #00ffff, #8b5cf6);
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, #8b5cf6, #00ffff);
}

/* Hide scrollbar for horizontal scroll */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Animated glow border keyframes */
@keyframes glow-pulse {
  0%, 100% {
    opacity: 0.3;
    transform: scale(1);
  }
  50% {
    opacity: 0.6;
    transform: scale(1.02);
  }
}

:root {
  --background: hsl(240, 100%, 3%);
  --foreground: hsl(0, 0%, 91%);
  --card: hsl(240, 100%, 6%);
  --card-foreground: hsl(0, 0%, 91%);
  --popover: hsl(240, 100%, 3%);
  --popover-foreground: hsl(0, 0%, 91%);
  --primary: hsl(180, 100%, 50%);
  --primary-foreground: hsl(240, 100%, 3%);
  --secondary: hsl(240, 5%, 15%);
  --secondary-foreground: hsl(0, 0%, 91%);
  --muted: hsl(240, 5%, 25%);
  --muted-foreground: hsl(240, 3%, 60%);
  --accent: hsl(270, 70%, 60%);
  --accent-foreground: hsl(0, 0%, 91%);
  --destructive: hsl(0, 70%, 60%);
  --destructive-foreground: hsl(0, 0%, 91%);
  --border: hsl(240, 5%, 20%);
  --input: hsl(240, 5%, 15%);
  --ring: hsl(180, 100%, 50%);

  /* Neon theme colors */
  --neon-cyan: hsl(180, 100%, 50%);
  --neon-purple: hsl(259, 83%, 68%);
  --neon-pink: hsl(330, 100%, 50%);
  --neon-green: hsl(120, 100%, 63%);
  --electric-blue: hsl(220, 100%, 50%);
  --space-dark: hsl(240, 50%, 6%);
  --carbon-black: hsl(0, 0%, 7%);

  --font-sans: 'Inter', sans-serif;
  --font-poppins: 'Poppins', sans-serif;
  --radius: 1rem;
}

.dark {
  --background: hsl(240, 100%, 3%);
  --foreground: hsl(0, 0%, 91%);
  --card: hsl(240, 100%, 6%);
  --card-foreground: hsl(0, 0%, 91%);
  --popover: hsl(240, 100%, 3%);
  --popover-foreground: hsl(0, 0%, 91%);
  --primary: hsl(180, 100%, 50%);
  --primary-foreground: hsl(240, 100%, 3%);
  --secondary: hsl(240, 5%, 15%);
  --secondary-foreground: hsl(0, 0%, 91%);
  --muted: hsl(240, 5%, 25%);
  --muted-foreground: hsl(240, 3%, 60%);
  --accent: hsl(270, 70%, 60%);
  --accent-foreground: hsl(0, 0%, 91%);
  --destructive: hsl(0, 70%, 60%);
  --destructive-foreground: hsl(0, 0%, 91%);
  --border: hsl(240, 5%, 20%);
  --input: hsl(240, 5%, 15%);
  --ring: hsl(180, 100%, 50%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-space-dark text-foreground overflow-x-hidden;
    background-color: var(--space-dark);
  }
}

@layer utilities {
  .glass-effect {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .subtle-glow {
    box-shadow: 0 4px 20px rgba(0, 255, 255, 0.1);
  }

  .card-3d {
    transition: all 0.3s ease-out;
    transform-style: preserve-3d;
  }

  .card-3d:hover {
    transform: perspective(1000px) rotateX(5deg) rotateY(5deg) translateZ(10px) scale(1.05);
    box-shadow: 0 20px 40px rgba(0, 255, 255, 0.15);
  }

  .category-card {
    transition: all 0.3s ease-out;
    transform-style: preserve-3d;
  }

  .category-card:hover {
    transform: perspective(1000px) rotateY(10deg) scale(1.1);
    box-shadow: 0 15px 30px rgba(139, 92, 246, 0.2);
  }

  .project-card {
    transition: all 0.3s ease-out;
    transform-style: preserve-3d;
  }

  .project-card:hover {
    transform: perspective(1000px) rotateX(8deg) translateZ(15px) scale(1.02);
    box-shadow: 0 25px 50px rgba(0, 255, 255, 0.2);
  }

  .gradient-text {
    background: linear-gradient(135deg, var(--neon-cyan), var(--neon-purple));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .hero-text {
    background: linear-gradient(135deg, #ffffff, var(--neon-cyan));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .floating-particles {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow: hidden;
    pointer-events: none;
  }

  .particle {
    position: absolute;
    border-radius: 50%;
    opacity: 0.7;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-subtle {
    animation: pulseSubtle 3s ease-in-out infinite alternate;
  }

  .button-press {
    transition: all 0.12s ease-out;
    position: relative;
    overflow: hidden;
  }

  .button-press:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 255, 255, 0.25);
  }

  .button-press:active {
    transform: scale(0.98) translateY(-1px);
    box-shadow: 0 4px 15px rgba(0, 255, 255, 0.4);
  }

  .button-press::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    transition: width 0.3s ease, height 0.3s ease, top 0.3s ease, left 0.3s ease;
    transform: translate(-50%, -50%);
  }

  .button-press:active::before {
    width: 100%;
    height: 100%;
  }

  .parallax-layer {
    transform-style: preserve-3d;
    transition: transform 0.1s ease-out;
  }

  .animate-rotate-slow {
    animation: rotate 10s linear infinite;
  }

  .animate-morph {
    animation: morph 8s ease-in-out infinite;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out;
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes pulseSubtle {
  0% { opacity: 0.8; transform: scale(1); }
  50% { opacity: 1; transform: scale(1.05); }
  100% { opacity: 0.8; transform: scale(1); }
}

@keyframes ripple {
  0% { transform: scale(0); opacity: 1; }
  100% { transform: scale(4); opacity: 0; }
}

@keyframes slideInUp {
  0% { transform: translateY(100%); opacity: 0; }
  100% { transform: translateY(0); opacity: 1; }
}

/* Line clamp utility */
.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* Enhanced modal animations */
.modal-backdrop {
  backdrop-filter: blur(12px);
  background: rgba(0, 0, 0, 0.8);
}

/* Enhanced project card effects */
.project-card-gradient {
  background: linear-gradient(135deg, rgba(0,255,255,0.02) 0%, rgba(139,92,246,0.02) 50%, rgba(255,0,128,0.02) 100%);
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes morph {
  0%, 100% { border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%; }
  50% { border-radius: 30% 60% 70% 40% / 50% 60% 30% 60%; }
}

@keyframes slideUp {
  0% { transform: translateY(100%); opacity: 0; }
  100% { transform: translateY(0); opacity: 1; }
}

@keyframes fadeIn {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

